mapper.insert:
	INSERT
	  INTO TBL_PRODUCT
	(
		PROD_NO
		,CATEGORY_NO
		,BRAND_NO
		,PROD_NAME
		,PROD_DESC
		,PROD_PRICE
		,DISCOUNT_RATE
		,PROD_DETAIL_CONTENT
		,PROD_DETAIL_VIEW_COUNT
		,PROD_INST_DATE
		,PROD_UPT_DATE	
		,PROD_AVAIL_YN
		,PROD_SIZE
		,PROD_COLOR 
	)
	VALUES
	(
	  SEQ_PROD_NO.NEXTVAL
	, #{ categoryNo}
	, #{ brandNo }
	, #{ prodName }
	, #{ prodDesc }
	, #{ prodPrice }
	, #{ discountRate }
	, #{ prodDetailContent } 
 	, DEFAULT
	, SYSDATE
	, SYSDATE
	, DEFAULT
	, #{ prodSize }
	, #{ prodColor } 
	)

mapper.update:
UPDATE TBL_PRODUCT
	SET CATEGORY_NO = #{categoryNo}
		, BRAND_NO = #{brandNo}
		, PROD_NAME = #{prodName}
		, PROD_DESC = #{prodDesc}
		, PROD_PRICE = #{prodPrice}
		, DISCOUNT_RATE = #{discountRate}
		, PROD_DETAIL_CONTENT = #{prodDetailContent}
		, PROD_UPT_DATE = SYSDATE 
		, PROD_SIZE = #{prodSize}
		, PROD_COLOR = #{prodColor}
	WHERE PROD_NO = #{ prodNo }

mapper.insert:
